setup.code:
from gavo import base from gavo import rscdef from gavo.protocols import sdm
META_BY_FRAME_TYPE = { # (units, ucds, descriptions) "celestial": ( ("deg", "deg", "m"), ("pos.eq.ra", "pos.eq.dec", "phys.distance"), ( "Right Ascension (ICRS)", "Declination (ICRS)", "Distance from coordiate origin")),
"body": ( ("deg", "deg", "m"), ("pos.bodyrc.long", "pos.bodyrc.lat", "pos.bodyrc.alt"), ( "Longitude on body", "Latitude on body", "Height over defined null")),
"cartesian": ( ("m", "m", "m"), ("pos.cartesian.x", "pos.cartesian.y", "pos.cartesian.z"), ( "Cartesian coordinate in x direction", "Cartesian coordinate in y direction", "Cartesian coordinate in z direction")),
"spherical": ( ("m", "deg", "deg"), ("phys.distance", "pos.az.zd", "pos.az.azi"), ( "Radial distance in spherical coordinates", "Polar angle or colatitude in spherical coordinates", "Azimuth in spherical coordinates")),
"cylindrical": ( ("m", "deg", "m"), ("pos", "pos.az.azi", "pos"), ( "Radial distance in cylindrical coordinates", "Azimuth in cylindrical coordinates", "Height in cylindrical coordinates")), }
def setFrameMeta(tableDef, spatialFrameType): if spatialFrameType not in META_BY_FRAME_TYPE: raise base.StructureError("Unknown EPN-TAP frame type: %s."% spatialFrameType, hint="Known frame types are: %s"%( ", ".join(META_BY_FRAME_TYPE)))
units, ucds, descriptions = META_BY_FRAME_TYPE[spatialFrameType] for cooIndex in range(3): prefix = "c%d"%(cooIndex+1) for postfix in ["min", "max", "_resol_min", "_resol_max"]: col = tableDef.getColumnByName(prefix+postfix) col.unit = col.unit.replace( "__replace_framed__", units[cooIndex]) col.ucd = col.ucd.replace( "__replace_framed__", ucds[cooIndex]) col.description = col.description.replace( "__replace_framed__", descriptions[cooIndex])
def addOptionalColumns(tableDef, columnNames): sourceTable = base.resolveCrossId("//epntap2#optional_columns") for columnName in columnNames.split(): tableDef.columns.append( sourceTable.getColumnByName(columnName))

LOOP.codeItems:
# overridden is a set of column names for which the parameters
# are manually defined above or which are set in some other way.
overridden = set(["index_",
	"target_name", "time_scale",
	"instrument_host_name", "instrument_name",
	"access_format", "target_region", "target_class"])

mixin = context.getById("table-2_0")
colDict = {}
for type, name, content, pos in mixin.events.events_:
	if type=="value":
		colDict[name] = content
	elif type=="end":
		if name=="column":
			if colDict.get("name") not in overridden:
				if colDict.get("required", False):
					colDict["default"] = ''
				else:
					colDict["default"] = 'None'
				yield colDict
			colDict = {}

setup.code:
